void collectiblesMenu::init() {
	setName("Collectibles");
	setParentSubmenu<teleportMenu>();


	addOption(numberOption<int>(SCROLL, "Cycle")
		.addNumber(vars.cycle, "%i", 1).addMin(1).addMax(6));

	addOption(breakOption("Collectibles"));

	addOption(scrollOption<math::vector3<float>[6]>(SCROLLSELECT, "Heirlooms")
		.addScroll(vars.heirlooms, 0, NUMOF(heirlooms), heirlooms)
		.addOnClick([] { teleportMenuVars::teleportToLocation(heirlooms[vars.heirlooms].result[vars.cycle - 1]); }));

	addOption(scrollOption<math::vector3<float>[6]>(SCROLLSELECT, "Cups")
		.addScroll(vars.cups, 0, NUMOF(cups), cups)
		.addOnClick([] { teleportMenuVars::teleportToLocation(cups[vars.cups].result[vars.cycle - 1]); }));

	addOption(scrollOption<math::vector3<float>[6]>(SCROLLSELECT, "Swords")
		.addScroll(vars.swords, 0, NUMOF(swords), swords)
		.addOnClick([] { teleportMenuVars::teleportToLocation(swords[vars.swords].result[vars.cycle - 1]); }));

	addOption(scrollOption<math::vector3<float>[6]>(SCROLLSELECT, "Wands")
		.addScroll(vars.wands, 0, NUMOF(wands), wands)
		.addOnClick([] { teleportMenuVars::teleportToLocation(wands[vars.wands].result[vars.cycle - 1]); }));

	addOption(scrollOption<math::vector3<float>[6]>(SCROLLSELECT, "Pentacles")
		.addScroll(vars.pentacles, 0, NUMOF(pentacles), pentacles)
		.addOnClick([] { teleportMenuVars::teleportToLocation(pentacles[vars.pentacles].result[vars.cycle - 1]); }));

}

/*Called while in submenu*/
void collectiblesMenu::update() {}

/*Called once on submenu open*/
void collectiblesMenu::updateOnce() {}

/*Called always*/
void collectiblesMenu::featureUpdate() {}

collectiblesMenu* _instance;
collectiblesMenu* collectiblesMenu::getInstance() {
	if (_instance == nullptr) {
		_instance = new collectiblesMenu();
		_instance->init();
		menu::getSubmenuHandler()->addSubmenu(_instance);
	}

	return _instance;
}

collectiblesMenu::~collectiblesMenu() { delete _instance; }